mapper.insert:
INSERT INTO 작물 (
	사용자ID, 작물명, 품종, 심은날짜, 예상수확일, 상태
) VALUES (
	#{사용자ID}, #{작물명}, #{품종}, #{심은날짜}, #{예상수확일}, #{상태}
)

mapper.select:
SELECT c.작물ID, c.사용자ID, c.작물명, c.품종, c.심은날짜, c.예상수확일, c.상태, u.이름 as 사용자이름 FROM 작물 c LEFT JOIN 사용자 u ON c.사용자ID = u.사용자ID
AND (c.작물명 LIKE CONCAT('%', #{검색키워드}, '%') OR c.품종 LIKE CONCAT('%', #{검색키워드}, '%') OR u.이름 LIKE CONCAT('%', #{검색키워드}, '%'))
AND c.상태 = #{상태필터}
AND c.사용자ID = #{사용자ID}
ORDER BY c.심은날짜 DESC

SELECT COUNT(*) FROM 작물 c LEFT JOIN 사용자 u ON c.사용자ID = u.사용자ID
AND (c.작물명 LIKE CONCAT('%', #{검색키워드}, '%') OR c.품종 LIKE CONCAT('%', #{검색키워드}, '%') OR u.이름 LIKE CONCAT('%', #{검색키워드}, '%'))
AND c.상태 = #{상태필터}
AND c.사용자ID = #{사용자ID}

SELECT COUNT(*) 
FROM 작물
WHERE 사용자ID = #{사용자ID}
