mapper.insert:
INSERT INTO FarmingDiary (
    user_id, crop_id, date, activity_type, content, photo_url
) VALUES (
    #{userId}, #{cropId}, #{date}, #{activityType}, #{content}, #{photoUrl}
)

mapper.select:
SELECT COUNT(*) 
FROM FarmingDiary
WHERE user_id = #{userId}

SELECT COUNT(*) 
FROM FarmingDiary
WHERE crop_id = #{cropId}
